Event 'DoAdd'

	&GAMUser.Load(&UserId)
	For each line
		If &Select
			&PermissionAdd.ApplicationId= &ApplicationId
			&PermissionAdd.GUID		  	= &Id
			&PermissionAdd.Type.FromString(&Access)
			&isOK = &GAMUser.AddPermission(&PermissionAdd, &Errors)
			If not &isOK
				For &Error in &Errors
					Msg(Format(!"%1 (GAM%2)", &Error.Message, &Error.Code))
				EndFor	
				Exit
			Endif		
		Endif
	EndFor
	If &isOK
		Commit
		GAMWWUserPermissions(&UserId, &ApplicationId)
	Else
		For &Error in &Errors
			Msg(Format(!"%1 (GAM%2)", &Error.Message, &Error.Code))
		EndFor		
	Endif

	/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */

EndEvent





