&Repository = GAMRepository.Get()
Do Case
Case &Repository.UserActivationMethod = GAMUserActivationMethod.User
	&Message = new()
	&Message.Type = MessageTypes.Warning
	&Message.Description = "The user was created successfully!!, you must confirm the account, check your email, you will receive an email with a link to activate your account."
	&Messages.Add(&Message)
	///////////////////////////////////////////////////////////////
	//Send mail to User with instructions to activate acount:
	&User.Load(&UserGUID)
	&ActivactionKey = &User.GetActivationKey(&Errors)
	//In order to activate the user account send an email to the user including a URL 
	//like the following : http://server/app/webpanel_ActivateAccount.aspx?&ActivactionKey.
	//&ActivactionKey is the key used to activate the account and is received as parameter.
	//In webpanel_ActivateAccount you have to activate the user's account using 
	//GAMRepository.ActivateUser method using the &ActivationKey as parameter.
	//Sample code to webpanel_ActivateAccount:
	//Event Start
	&isOK = GAMRepository.ActivateUser(&ActivactionKey, True, &Errors)
	If &isOK
		&Message = new()
		&Message.Type = MessageTypes.Warning
		&Message.Description = "Your user account was successfully activated !!"
		&Messages.Add(&Message)
	Else
		&Messages = GAMConvertErrorsToMessages(&Errors)
	Endif
	//EndEvent
	///////////////////////////////////////////////////////////////

Case &Repository.UserActivationMethod = GAMUserActivationMethod.Administrator
	///////////////////////////////////////////////////////////////
	//Send mail to Administartor (&Repository.EmailSecurityAdministrator) with instructions to activate User
	///////////////////////////////////////////////////////////////
	&Message = new()
	&Message.Type = MessageTypes.Warning
	&Message.Description = "The user was created successfully!!, you must wait for confirmation from the administrator."
	&Messages.Add(&Message)	
EndCase


